import React, {Component, Fragment} from 'react';
import {Col, Container, Form, Row} from "react-bootstrap";
import Porzotok from "../../asset/images/Porzotok.png";
import {Redirect} from "react-router-dom";
import {toast} from "react-toastify";
import axios from "axios";
import ApiUrl from "../../api/ApiURL";

class ChangePassword extends Component {
    constructor(props) {
        super(props);
        this.state={
            current_pass:"",
            new_pass:"",
            confirm_pass:"",
            userProfileRedirect:false,
        };
    }

    onUserProfileRedirect(){
        if(this.state.userProfileRedirect===true){
            return(
                <Redirect to="/profile"/>
            )
        }
    }

    currentPasswordOnChange=(event)=>{
        let user_mobile=event.target.value;
        this.setState(({user_mobile:user_mobile}))
    }

    newPasswordOnChange=(event)=>{
        let user_password=event.target.value;
        this.setState(({user_password:user_password}))
    }

    onChangePasswordSubmit=(event)=>{
        let current_pass=this.state.current_pass;
        let new_pass=this.state.new_pass;
        let confirm_pass=this.state.confirm_pass;

        let changePassBtn=document.getElementById('changePassBtn');
        let ChangePasswordForm=document.getElementById('ChangePasswordForm');

        if(current_pass.length===0){
            toast.error('Current Password is Required!', {
                position: "top-center",
                theme:"colored",
                autoClose: 2000,
            });
        }
        else if(new_pass.length===0){
            toast.error('New Password is Required!', {
                position: "top-center",
                theme:"colored",
                autoClose: 3000,
            });
        }
        else if(confirm_pass.length===0){
            toast.error('Confirm Password is Required!', {
                position: "top-center",
                theme:"colored",
                autoClose: 3000,
            });
        }
        else if(new_pass!==confirm_pass){
            toast.error('Confirm Password does not match!', {
                position: "top-center",
                theme:"colored",
                autoClose: 3000,
            });
        }
        else{
            ChangePasswordForm.innerHTML="Sending...";
            let MyFormData=new FormData();
            MyFormData.append("mobile",current_pass);
            MyFormData.append("password",new_pass);

            axios.post(ApiUrl.changePassword,MyFormData).then((response)=>{
                if(response.status===200){
                    toast.success('Login Successful', {
                        position: "top-center",
                        theme:"colored",
                        autoClose: 2000,
                    });
                    this.setState({userProfileRedirect:true});
                    ChangePasswordForm.reset();
                }

                else if(response.status===400){
                    toast.error('Phone Number or Password Not Match!', {
                        position: "top-center",
                        theme:"colored",
                        autoClose: 2000,
                    });
                    changePassBtn.innerHTML="Login";
                }
                else if(response.status===201){
                    sessionStorage.setItem("UserIDForVerify",response.data.data.user_id);
                    this.setState({userOtpVerificationRedirect:true});
                    ChangePasswordForm.reset();
                }

                else{
                    toast.error('Login Fail ! Try Again', {
                        position: "top-center",
                        theme:"colored",
                        autoClose: 2000,
                    });
                    changePassBtn.innerHTML="Login";
                }
            }).catch((error)=> {
                toast.error('Login Fail ! Try Again', {
                    position: "top-center",
                    theme:"colored",
                    autoClose: 2000,
                });
                changePassBtn.innerHTML="Login";
            });
        }
        event.preventDefault();
    }
    render() {
        return (
            <Fragment>
                <Container className="p-5 d-flex justify-content-center">
                    <Row className="LoginCard shadow-sm">
                        <Col xl={10} lg={10} md={10} sm={12} xs={12}>
                            <Form id="ChangePasswordForm" onSubmit={this.onChangePasswordSubmit}>
                                <img className="LoginCardPorzotokImg mt-4" src={Porzotok} alt=""/>
                                <div className="form-group">
                                    <input type="text" className="form-control placeholder-text" placeholder="Enter Current Password"/>
                                </div>
                                <div className="form-group">
                                    <input type="text" className="form-control placeholder-text" placeholder="Enter New Password"/>
                                </div>
                                <div className="form-group">
                                    <input type="text" className="form-control placeholder-text" placeholder="Enter Confirm Password"/>
                                </div>
                                <button id="changePassBtn" className="btn SendBtnColorText mb-5 btn-block">Change Password</button>
                            </Form>
                        </Col>
                    </Row>
                </Container>
            </Fragment>
        );
    }
}

export default ChangePassword;
