import React, {Component, Fragment} from 'react';
import {Col, Container, Form, Row} from "react-bootstrap";
import Porzotok from "../../asset/images/Porzotok.png";
import {Link, Redirect} from "react-router-dom";
import SessionHelper from "../../SessionHelper/SessionHelper";
import {toast} from "react-toastify";
class UserVerification extends Component {
    constructor() {
        super();
        this.state={
            user_id: SessionHelper.GetUserID('UserID'),
            OtpCode:"",
            userRedirect:false,
        }
        this.onUserRedirect=this.onUserRedirect.bind(this);
    }

    OtpOnChange=(event)=>{
        let OtpCode=event.target.value;
        this.setState(({OtpCode:OtpCode}));
    }

    onUserRedirect(){
        if(this.state.userRedirect===true){
            return(
                <Redirect to="/"/>
            )
        }
    }

    onOTPFromSubmit = (event) => {
        let UserID = this.state.user_id;
        let OtpCode = this.state.OtpCode;

        let sendBtn = document.getElementById('sendBtn');
        let LoginForm = document.getElementById('LoginForm');

        /*validation Check*/
        if (OtpCode.length === 6) {
            toast.error('Email is Required !', {
                position: "bottom-center",
                theme: "colored",
                autoClose: false,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
            });
        } else {
            sendBtn.innerHTML = "Sending...";

            /*
            //for Api Form-data
            let MyFormData = new FormData();
            MyFormData.append("email", email);
            MyFormData.append("password", password);*/

            let config = {
                headers: {'content-type': 'multipart/form-data'}
            }

            axios.post(ApiURL.SendLogin,MyFormDataJson).then((response)=> {
                if (response.status === 200 && response.data.login===1) {
                    sendBtn.innerHTML = "Login";
                    SessionHelper.SetUserEmail(email);

                    sessionStorage.setItem("Fullname",response.data.Fullname);

                    toast.success('successfully login', {
                        position: "bottom-center",
                        theme: "colored",
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        autoClose: 3000,
                    });
                    this.setState({userRedirect:true});
                    LoginForm.reset();
                }
            }).catch((error)=> {
                toast.error('Request Fail ! Try Again', {
                    position: "bottom-center",
                    theme: "colored",
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    autoClose: 3000,
                });
                sendBtn.innerHTML = "Login";
            })
        }
        event.preventDefault();
    }


    render() {
        return (
            <Fragment>
                <Container className="p-5 d-flex justify-content-center">
                    <Row className="LoginCard shadow-sm">
                        <Col xl={1} lg={1} md={1} sm={12} xs={12}>

                        </Col>
                        <Col xl={10} lg={10} md={10} sm={12} xs={12}>
                            <Form on>
                                <img className="LoginCardPorzotokImg mt-4" src={Porzotok} alt=""/>
                                <div className="form-group">
                                    <input type="text" onChange={this.OtpOnChange} className="form-control placeholder-text" placeholder="Enter Your OTP"/>
                                </div>
                                <h1> <Link to="/userLogin" type="submit" id="OtpBtn" className="btn SendBtnColorText mb-5 btn-block">OTP VERIFICATION</Link> </h1>
                            </Form>
                            <h1 className="forgotText text-center mb-5 mt-3"> <p className="signUpText">Resend</p> </h1>
                        </Col>
                        <Col xl={1} lg={1} md={1} sm={12} xs={12}>

                        </Col>
                    </Row>
                </Container>
            </Fragment>
        );
    }
}

export default UserVerification;
